import React from 'react';

const Gallery = ({ folder, title }) => {
  const mediaCount = 50;
  const media = [];
  for (let i = 1; i <= mediaCount; i++) {
    ['jpg', 'jpeg', 'mp4'].forEach(ext => {
      const fileName = `${folder}_${String(i).padStart(2, '0')}.${ext}`;
      media.push(fileName);
    });
  }

  return (
    <div style={{ padding: '2rem', background: '#111', color: 'white' }}>
      <h2>{title} Gallery</h2>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
        gap: '1.5rem',
        marginTop: '1.5rem'
      }}>
        {media.map((file, idx) =>
          file.endsWith('.mp4') ? (
            <video
              key={idx}
              src={`/assets/${folder}/${file}`}
              controls
              poster={`/assets/${folder}/${file.replace('.mp4', '.jpg')}`}
              style={{ width: '100%', borderRadius: '12px', objectFit: 'cover' }}
              onError={(e) => e.target.poster = '/assets/home/default-thumbnail.jpg'}
            />
          ) : (
            <img
              key={idx}
              src={`/assets/${folder}/${file}`}
              alt={file}
              style={{ width: '100%', borderRadius: '12px', objectFit: 'cover' }}
              onError={(e) => e.target.style.display = 'none'}
            />
          )
        )}
      </div>
    </div>
  );
};

export default Gallery;
